import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useTable } from 'react-table';
import './DataTable.css';

const DataTable = ({ data, onDataUpdate, summary, readOnly = false, isForecasted = false }) => {
  const [tableData, setTableData] = useState([]);
  const [updateTimeout, setUpdateTimeout] = useState(null);
  const [showSummary, setShowSummary] = useState(true);

  // Initialize table data when data prop changes
  useEffect(() => {
    if (data && data.length > 0) {
      setTableData(data);
    } else {
      setTableData([]);
    }
  }, [data]);

  // Function to update parent component with debounce
  const updateParent = useCallback((newData) => {
    if (updateTimeout) {
      clearTimeout(updateTimeout);
    }

    const timeoutId = setTimeout(() => {
      if (onDataUpdate) {
        onDataUpdate(newData);
      }
    }, 300); // Reduced debounce time for better responsiveness

    setUpdateTimeout(timeoutId);
  }, [onDataUpdate, updateTimeout]);

  // Clean up timeout on unmount
  useEffect(() => {
    return () => {
      if (updateTimeout) {
        clearTimeout(updateTimeout);
      }
    };
  }, [updateTimeout]);

  // Handle cell value change
  const handleCellChange = useCallback((rowId, columnId, value, isNumeric) => {
    setTableData(prevData => {
      const newData = prevData.map(row => {
        if (row.id === rowId) {
          return {
            ...row,
            [columnId]: value
          };
        }
        return row;
      });

      return newData;
    });

    // Only update parent after a short delay to prevent cursor jumping
    const timer = setTimeout(() => {
      setTableData(prevData => {
        const formattedData = prevData.map(row => {
          const newRow = {...row};

          // Format numeric fields only when updating parent
          if (row.id === rowId && isNumeric && value !== '') {
            newRow[columnId] = parseFloat(value);
          }

          return newRow;
        });

        updateParent(formattedData);
        return formattedData;
      });
    }, 200); // Reduced delay for better responsiveness

    return () => clearTimeout(timer);
  }, [updateParent]);

  // Define columns based on data structure with improved headers
  const columns = useMemo(() => {
    if (!tableData || tableData.length === 0) return [];

    const firstRow = tableData[0];
    return Object.keys(firstRow)
      .filter(key => key !== 'id') // Exclude id from display
      .map(key => {
        // Determine if column is numeric
        const isNumeric = ['energy_kwh', 'production_units', 'transport_km', 'waste_kg', 
                           'water_m3', 'fuel_l', 'grid_intensity', 'y', 
                           'predicted_emissions', 'lower_bound', 'upper_bound'].includes(key);

        // Format header with units
        let header = key;
        if (key === 'date' || key === 'ds') {
          header = 'Date';
        } else if (key === 'energy_kwh') {
          header = 'Energy Use (kWh)';
        } else if (key === 'transport_km') {
          header = 'Transport (km)';
        } else if (key === 'waste_kg') {
          header = 'Waste (tons)';
        } else if (key === 'water_m3') {
          header = 'Water (liters)';
        } else if (key === 'fuel_l') {
          header = 'Fuel (liters)';
        } else if (key === 'y') {
          header = 'Emissions (tons CO2e)';
        } else if (key === 'production_units') {
          header = 'Production (units)';
        } else if (key === 'grid_intensity') {
          header = 'Grid Intensity (kg CO2e/kWh)';
        } else {
          header = key.split('_').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
        }

        return {
          Header: header,
          accessor: key,
          Cell: ({ value, row }) => {
            if (readOnly) {
              // Format date if it's a date column
              if (key === 'date' || key === 'ds') {
                return new Date(value).toLocaleDateString();
              }

              // Format numeric values
              if (isNumeric) {
                return typeof value === 'number' ? value.toFixed(2) : value;
              }

              return value;
            }

            return (
              <input
                type={isNumeric ? 'text' : 'text'} // Use text for all to prevent browser validation
                value={value || ''}
                onChange={e => handleCellChange(row.original.id, key, e.target.value, isNumeric)}
                onBlur={e => {
                  if (isNumeric && e.target.value !== '') {
                    // Format on blur
                    const numValue = parseFloat(e.target.value);
                    if (!isNaN(numValue)) {
                      handleCellChange(row.original.id, key, numValue, isNumeric);
                    }
                  }
                }}
                className={`cell-input ${isNumeric ? 'numeric' : ''} ${
                  isNumeric && value !== '' && isNaN(parseFloat(value)) ? 'invalid' : ''
                }`}
                disabled={readOnly}
              />
            );
          }
        };
      });
  }, [tableData, readOnly, handleCellChange]);

  // Create table instance
  const tableInstance = useTable({ columns, data: tableData });

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = tableInstance;

  // Add a new row to the table
  const handleAddRow = useCallback(() => {
    if (!tableData || tableData.length === 0) return;

    const newRow = {};
    const lastRow = tableData[tableData.length - 1];

    // Initialize with empty values
    Object.keys(lastRow).forEach(key => {
      if (key === 'id') {
        newRow[key] = Date.now(); // Use timestamp as unique id
      } else if (key === 'date' || key === 'ds') {
        // Set date to today
        newRow[key] = new Date().toISOString().split('T')[0];
      } else {
        newRow[key] = '';
      }
    });

    const newData = [...tableData, newRow];
    setTableData(newData);
    // Don't update parent immediately to prevent cursor issues
    setTimeout(() => updateParent(newData), 100);
  }, [tableData, updateParent]);

  // Delete a row from the table
  const handleDeleteRow = useCallback((id) => {
    const newData = tableData.filter(row => row.id !== id);
    setTableData(newData);
    updateParent(newData);
  }, [tableData, updateParent]);

  // Toggle summary visibility
  const toggleSummary = () => {
    setShowSummary(!showSummary);
  };

  // Validate numeric input
  const validateNumeric = (value) => {
    if (value === '' || value === null) return true;
    return !isNaN(parseFloat(value)) && isFinite(value);
  };

  // Format date for display
  const formatDate = (dateString) => {
    if (!dateString) return '';

    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric'
      });
    } catch (error) {
      return dateString;
    }
  };

  // Format number for display
  const formatNumber = (value, decimals = 2) => {
    if (value === null || value === undefined || value === '') return '';

    const num = parseFloat(value);
    if (isNaN(num)) return value;

    return num.toLocaleString('en-US', {
      minimumFractionDigits: decimals,
      maximumFractionDigits: decimals
    });
  };

  // Get column header with units
  const getColumnHeader = (column) => {
    switch (column) {
      case 'energy_kwh': return 'Energy Use (kWh)';
      case 'transport_km': return 'Transport (km)';
      case 'waste_kg': return 'Waste (tons)';
      case 'water_m3': return 'Water (liters)';
      case 'fuel_l': return 'Fuel (liters)';
      case 'y': return 'Emissions (tons CO2e)';
      case 'production_units': return 'Production (units)';
      case 'grid_intensity': return 'Grid Intensity (kg CO2e/kWh)';
      default: return column.charAt(0).toUpperCase() + column.slice(1).replace('_', ' ');
    }
  };

  // Render summary metrics
  const renderSummaryMetrics = () => {
    if (!summary || Object.keys(summary.mean).length === 0) {
      return <p>No data available for summary</p>;
    }

    const columns = [
      'energy_kwh', 'transport_km', 'waste_kg', 'water_m3', 
      'fuel_l', 'y', 'production_units', 'grid_intensity'
    ];

    return (
      <div className="summary-metrics">
        {columns.map(col => (
          <div className="metric-card" key={col}>
            <h4>{getColumnHeader(col)}</h4>
            <div className="metric-values">
              <div className="metric-value">
                <span className="metric-value-label">Mean</span>
                <span className="metric-value-number">
                  {formatNumber(summary.mean[col])}
                </span>
              </div>
              <div className="metric-value">
                <span className="metric-value-label">Min</span>
                <span className="metric-value-number">
                  {formatNumber(summary.min[col])}
                </span>
              </div>
              <div className="metric-value">
                <span className="metric-value-label">Max</span>
                <span className="metric-value-number highlight">
                  {formatNumber(summary.max[col])}
                </span>
              </div>
              <div className="metric-value">
                <span className="metric-value-label">Std Dev</span>
                <span className="metric-value-number">
                  {formatNumber(summary.std[col])}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="data-table-container">
      <div className="table-controls">
        {!readOnly && (
          <button className="add-row-btn" onClick={handleAddRow}>
            Add Row
          </button>
        )}
        {summary && !isForecasted && (
          <button className="toggle-summary-btn" onClick={toggleSummary}>
            {showSummary ? 'Hide Summary' : 'Show Summary'}
          </button>
        )}
      </div>

      <div className="table-wrapper">
        <table {...getTableProps()} className="data-table">
          <thead>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                  <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                ))}
                {!readOnly && <th className="action-column">Actions</th>}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map(row => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()}>
                  {row.cells.map(cell => (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  ))}
                  {!readOnly && (
                    <td className="action-cell">
                      <button 
                        className="delete-row-btn" 
                        onClick={() => handleDeleteRow(row.original.id)}
                      >
                        Delete
                      </button>
                    </td>
                  )}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {summary && !isForecasted && showSummary && (
        <div className="data-summary-card">
          <h3>Data Summary Statistics</h3>
          {renderSummaryMetrics()}
        </div>
      )}
    </div>
  );
};

export default DataTable;
