import React, { useEffect, useRef } from 'react';
import './ForecastChart.css';

const ForecastChart = ({ data, forecast, optimizedForecast, impacts }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (!forecast || !chartRef.current) return;

    // Clear any previous content
    chartRef.current.innerHTML = '';

    // Create a simple canvas-based chart without Chart.js
    const createSimpleChart = () => {
      const container = chartRef.current;

      // Format data for display
      const actualData = data ? data.map(d => ({
        date: new Date(d.date),
        value: parseFloat(d.emissions) || 0
      })).sort((a, b) => a.date - b.date) : [];

      const forecastData = forecast.map(d => ({
        date: new Date(d.ds),
        value: d.predicted_emissions,
        lower: d.lower_bound,
        upper: d.upper_bound
      }));

      const optimizedData = optimizedForecast ? optimizedForecast.map(d => ({
        date: new Date(d.ds),
        value: d.predicted_emissions
      })) : [];

      // Find min and max values for scaling
      const allValues = [
        ...actualData.map(d => d.value),
        ...forecastData.map(d => d.value),
        ...forecastData.map(d => d.upper),
        ...optimizedData.map(d => d.value)
      ];

      const minValue = Math.min(...allValues) * 0.9;
      const maxValue = Math.max(...allValues) * 1.1;

      // Find date range
      const allDates = [
        ...actualData.map(d => d.date),
        ...forecastData.map(d => d.date),
        ...optimizedData.map(d => d.date)
      ];

      const minDate = new Date(Math.min(...allDates));
      const maxDate = new Date(Math.max(...allDates));

      // Create chart elements
      const chartTitle = document.createElement('h3');
      chartTitle.className = 'chart-title';
      chartTitle.textContent = 'Carbon Emissions Forecast';
      container.appendChild(chartTitle);

      // Create chart container
      const chartContainer = document.createElement('div');
      chartContainer.className = 'simple-chart-container';
      container.appendChild(chartContainer);

      // Create Y-axis labels
      const yAxis = document.createElement('div');
      yAxis.className = 'y-axis';

      const numYLabels = 5;
      for (let i = 0; i < numYLabels; i++) {
        const label = document.createElement('div');
        label.className = 'y-label';
        const value = maxValue - (i * (maxValue - minValue) / (numYLabels - 1));
        label.textContent = value.toFixed(1);
        label.style.bottom = `${(i / (numYLabels - 1)) * 100}%`;
        yAxis.appendChild(label);
      }

      chartContainer.appendChild(yAxis);

      // Create chart area
      const chartArea = document.createElement('div');
      chartArea.className = 'chart-area';
      chartContainer.appendChild(chartArea);

      // Create X-axis labels
      const xAxis = document.createElement('div');
      xAxis.className = 'x-axis';

      // Calculate months between min and max date
      const monthDiff = (maxDate.getFullYear() - minDate.getFullYear()) * 12 + 
                         (maxDate.getMonth() - minDate.getMonth());

      // Create a label every 2-3 months
      const labelInterval = Math.max(1, Math.floor(monthDiff / 5));

      for (let i = 0; i <= monthDiff; i += labelInterval) {
        const date = new Date(minDate);
        date.setMonth(date.getMonth() + i);

        const label = document.createElement('div');
        label.className = 'x-label';
        label.textContent = `${date.toLocaleString('default', { month: 'short' })} ${date.getFullYear()}`;
        label.style.left = `${(i / monthDiff) * 100}%`;
        xAxis.appendChild(label);
      }

      chartContainer.appendChild(xAxis);

      // Function to convert data point to position
      const getPosition = (date, value) => {
        const x = ((date - minDate) / (maxDate - minDate)) * 100;
        const y = 100 - ((value - minValue) / (maxValue - minValue)) * 100;
        return { x, y };
      };

      // Create data points and lines

      // Actual data line
      if (actualData.length > 0) {
        const actualLine = document.createElement('div');
        actualLine.className = 'data-line actual-line';
        chartArea.appendChild(actualLine);

        let pathD = '';

        actualData.forEach((point, i) => {
          const pos = getPosition(point.date, point.value);

          // Create point
          const dataPoint = document.createElement('div');
          dataPoint.className = 'data-point actual-point';
          dataPoint.style.left = `${pos.x}%`;
          dataPoint.style.top = `${pos.y}%`;

          // Add tooltip
          dataPoint.setAttribute('data-tooltip', 
            `${point.date.toLocaleDateString()}: ${point.value.toFixed(2)} tons CO2e`);

          chartArea.appendChild(dataPoint);

          // Build path
          if (i === 0) {
            pathD = `M ${pos.x} ${pos.y}`;
          } else {
            pathD += ` L ${pos.x} ${pos.y}`;
          }
        });

        // Create SVG for line
        const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
        svg.setAttribute('width', '100%');
        svg.setAttribute('height', '100%');
        svg.style.position = 'absolute';
        svg.style.top = '0';
        svg.style.left = '0';

        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        path.setAttribute('d', pathD);
        path.setAttribute('fill', 'none');
        path.setAttribute('stroke', '#00FFFF');
        path.setAttribute('stroke-width', '2');

        svg.appendChild(path);
        actualLine.appendChild(svg);
      }

      // Forecast data line
      if (forecastData.length > 0) {
        const forecastLine = document.createElement('div');
        forecastLine.className = 'data-line forecast-line';
        chartArea.appendChild(forecastLine);

        let pathD = '';

        forecastData.forEach((point, i) => {
          const pos = getPosition(point.date, point.value);

          // Create point
          const dataPoint = document.createElement('div');
          dataPoint.className = 'data-point forecast-point';
          dataPoint.style.left = `${pos.x}%`;
          dataPoint.style.top = `${pos.y}%`;

          // Add tooltip
          dataPoint.setAttribute('data-tooltip', 
            `${point.date.toLocaleDateString()}: ${point.value.toFixed(2)} tons CO2e`);

          chartArea.appendChild(dataPoint);

          // Build path
          if (i === 0) {
            pathD = `M ${pos.x} ${pos.y}`;
          } else {
            pathD += ` L ${pos.x} ${pos.y}`;
          }

          // Add confidence interval
          if (point.lower && point.upper) {
            const lowerPos = getPosition(point.date, point.lower);
            const upperPos = getPosition(point.date, point.upper);

            const interval = document.createElement('div');
            interval.className = 'confidence-interval';
            interval.style.left = `${pos.x}%`;
            interval.style.top = `${upperPos.y}%`;
            interval.style.height = `${lowerPos.y - upperPos.y}%`;

            chartArea.appendChild(interval);
          }
        });

        // Create SVG for line
        const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
        svg.setAttribute('width', '100%');
        svg.setAttribute('height', '100%');
        svg.style.position = 'absolute';
        svg.style.top = '0';
        svg.style.left = '0';

        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        path.setAttribute('d', pathD);
        path.setAttribute('fill', 'none');
        path.setAttribute('stroke', '#FF00FF');
        path.setAttribute('stroke-width', '2');

        svg.appendChild(path);
        forecastLine.appendChild(svg);
      }

      // Optimized data line
      if (optimizedData.length > 0) {
        const optimizedLine = document.createElement('div');
        optimizedLine.className = 'data-line optimized-line';
        chartArea.appendChild(optimizedLine);

        let pathD = '';

        optimizedData.forEach((point, i) => {
          const pos = getPosition(point.date, point.value);

          // Create point
          const dataPoint = document.createElement('div');
          dataPoint.className = 'data-point optimized-point';
          dataPoint.style.left = `${pos.x}%`;
          dataPoint.style.top = `${pos.y}%`;

          // Add tooltip
          dataPoint.setAttribute('data-tooltip', 
            `${point.date.toLocaleDateString()}: ${point.value.toFixed(2)} tons CO2e (optimized)`);

          chartArea.appendChild(dataPoint);

          // Build path
          if (i === 0) {
            pathD = `M ${pos.x} ${pos.y}`;
          } else {
            pathD += ` L ${pos.x} ${pos.y}`;
          }
        });

        // Create SVG for line
        const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
        svg.setAttribute('width', '100%');
        svg.setAttribute('height', '100%');
        svg.style.position = 'absolute';
        svg.style.top = '0';
        svg.style.left = '0';

        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        path.setAttribute('d', pathD);
        path.setAttribute('fill', 'none');
        path.setAttribute('stroke', '#00FF00');
        path.setAttribute('stroke-width', '2');
        path.setAttribute('stroke-dasharray', '5,5');

        svg.appendChild(path);
        optimizedLine.appendChild(svg);
      }

      // Create legend
      const legend = document.createElement('div');
      legend.className = 'chart-legend';

      if (actualData.length > 0) {
        const actualLegend = document.createElement('div');
        actualLegend.className = 'legend-item';

        const actualColor = document.createElement('div');
        actualColor.className = 'legend-color actual-color';

        const actualLabel = document.createElement('div');
        actualLabel.className = 'legend-label';
        actualLabel.textContent = 'Actual Emissions';

        actualLegend.appendChild(actualColor);
        actualLegend.appendChild(actualLabel);
        legend.appendChild(actualLegend);
      }

      const forecastLegend = document.createElement('div');
      forecastLegend.className = 'legend-item';

      const forecastColor = document.createElement('div');
      forecastColor.className = 'legend-color forecast-color';

      const forecastLabel = document.createElement('div');
      forecastLabel.className = 'legend-label';
      forecastLabel.textContent = 'Forecast Emissions';

      forecastLegend.appendChild(forecastColor);
      forecastLegend.appendChild(forecastLabel);
      legend.appendChild(forecastLegend);

      if (optimizedData.length > 0) {
        const optimizedLegend = document.createElement('div');
        optimizedLegend.className = 'legend-item';

        const optimizedColor = document.createElement('div');
        optimizedColor.className = 'legend-color optimized-color';

        const optimizedLabel = document.createElement('div');
        optimizedLabel.className = 'legend-label';
        optimizedLabel.textContent = 'Optimized Emissions';

        optimizedLegend.appendChild(optimizedColor);
        optimizedLegend.appendChild(optimizedLabel);
        legend.appendChild(optimizedLegend);
      }

      container.appendChild(legend);
    };

    createSimpleChart();

    // Add impact factors section
    if (impacts && Object.keys(impacts).length > 0) {
      const impactSection = document.createElement('div');
      impactSection.className = 'impact-factors';

      const impactTitle = document.createElement('h4');
      impactTitle.textContent = 'Key Impact Factors';
      impactSection.appendChild(impactTitle);

      const impactList = document.createElement('div');
      impactList.className = 'impact-list';

      // Sort impacts by score
      const sortedImpacts = Object.entries(impacts)
        .sort((a, b) => Math.abs(b[1].impact_score) - Math.abs(a[1].impact_score))
        .slice(0, 3);

      sortedImpacts.forEach(([factor, data], index) => {
        let factorName = factor;
        if (factor === 'energy_use') factorName = 'Energy Use';
        if (factor === 'transport') factorName = 'Transport';
        if (factor === 'waste') factorName = 'Waste';
        if (factor === 'water') factorName = 'Water';
        if (factor === 'fuel') factorName = 'Fuel';
        if (factor === 'grid_intensity') factorName = 'Grid Intensity';

        const impactItem = document.createElement('div');
        impactItem.className = 'impact-item';

        const impactRank = document.createElement('div');
        impactRank.className = 'impact-rank';
        impactRank.textContent = index + 1;

        const impactInfo = document.createElement('div');
        impactInfo.className = 'impact-info';

        const impactName = document.createElement('div');
        impactName.className = 'impact-name';
        impactName.textContent = factorName;

        const impactScore = document.createElement('div');
        impactScore.className = 'impact-score';

        const impactValue = document.createElement('span');
        impactValue.className = 'impact-value';
        impactValue.textContent = Math.abs(data.impact_score).toFixed(2);

        impactScore.textContent = 'Impact Score: ';
        impactScore.appendChild(impactValue);

        impactInfo.appendChild(impactName);
        impactInfo.appendChild(impactScore);

        impactItem.appendChild(impactRank);
        impactItem.appendChild(impactInfo);

        impactList.appendChild(impactItem);
      });

      impactSection.appendChild(impactList);
      chartRef.current.appendChild(impactSection);
    }

  }, [data, forecast, optimizedForecast, impacts]);

  return (
    <div className="forecast-chart-container" ref={chartRef}></div>
  );
};

export default ForecastChart;
