import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import 'chartjs-adapter-date-fns';
import './ForecastChart.css';

const ForecastChart = ({ data, forecast, optimizedForecast, impacts }) => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  // Clean up chart on unmount
  useEffect(() => {
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
        chartInstance.current = null;
      }
    };
  }, []);

  useEffect(() => {
    if (!forecast || !chartRef.current) return;

    // Destroy previous chart if it exists
    if (chartInstance.current) {
      chartInstance.current.destroy();
      chartInstance.current = null;
    }

    const ctx = chartRef.current.getContext('2d');

    // Format data for chart
    const formatData = () => {
      // Process actual data from the data prop
      const actualDates = data ? data.map(d => new Date(d.date)) : [];
      const actualValues = data ? data.map(d => {
        const val = d.emissions;
        return typeof val === 'number' ? val : parseFloat(val) || 0;
      }) : [];

      // Process forecast data
      const forecastDates = forecast.map(d => new Date(d.ds));
      const forecastValues = forecast.map(d => {
        const val = d.predicted_emissions;
        return typeof val === 'number' ? val : parseFloat(val) || 0;
      });

      // Process bounds if available
      let lowerBounds = [];
      let upperBounds = [];
      if (forecast[0] && 'lower_bound' in forecast[0]) {
        lowerBounds = forecast.map(d => {
          const val = d.lower_bound;
          return typeof val === 'number' ? val : parseFloat(val) || 0;
        });
        upperBounds = forecast.map(d => {
          const val = d.upper_bound;
          return typeof val === 'number' ? val : parseFloat(val) || 0;
        });
      }

      // Process optimized data if available
      let optimizedValues = [];
      let optimizedDates = [];
      if (optimizedForecast) {
        optimizedDates = optimizedForecast.map(d => new Date(d.ds));
        optimizedValues = optimizedForecast.map(d => {
          const val = d.predicted_emissions;
          return typeof val === 'number' ? val : parseFloat(val) || 0;
        });
      }

      return {
        actualDates,
        actualValues,
        forecastDates,
        forecastValues,
        lowerBounds,
        upperBounds,
        optimizedDates,
        optimizedValues
      };
    };

    const {
      actualDates,
      actualValues,
      forecastDates,
      forecastValues,
      lowerBounds,
      upperBounds,
      optimizedDates,
      optimizedValues
    } = formatData();

    // Create datasets
    const datasets = [];

    // Add actual data if available
    if (actualDates.length > 0) {
      datasets.push({
        label: 'Actual Emissions',
        data: actualDates.map((date, index) => ({
          x: date,
          y: actualValues[index]
        })),
        borderColor: '#00FFFF', // Cyan
        backgroundColor: 'rgba(0, 255, 255, 0.1)',
        borderWidth: 2,
        pointBackgroundColor: '#00FFFF',
        pointBorderColor: '#00FFFF',
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4,
        fill: false
      });
    }

    // Add forecast data
    datasets.push({
      label: 'Forecast Emissions',
      data: forecastDates.map((date, index) => ({
        x: date,
        y: forecastValues[index]
      })),
      borderColor: '#FF00FF', // Magenta
      backgroundColor: 'rgba(255, 0, 255, 0.1)',
      borderWidth: 2,
      pointBackgroundColor: '#FF00FF',
      pointBorderColor: '#FF00FF',
      pointRadius: 4,
      pointHoverRadius: 6,
      tension: 0.4,
      fill: false
    });

    // Add confidence interval if available
    if (lowerBounds.length > 0 && upperBounds.length > 0) {
      datasets.push({
        label: 'Confidence Interval',
        data: forecastDates.map((date, index) => ({
          x: date,
          y: (lowerBounds[index] + upperBounds[index]) / 2,
          min: lowerBounds[index],
          max: upperBounds[index]
        })),
        backgroundColor: 'rgba(255, 0, 255, 0.2)',
        borderWidth: 0,
        pointRadius: 0,
        fill: true
      });
    }

    // Add optimized forecast if available
    if (optimizedValues.length > 0) {
      datasets.push({
        label: 'Optimized Emissions',
        data: optimizedDates.map((date, index) => ({
          x: date,
          y: optimizedValues[index]
        })),
        borderColor: '#00FF00', // Green
        backgroundColor: 'rgba(0, 255, 0, 0.1)',
        borderWidth: 2,
        borderDash: [5, 5],
        pointBackgroundColor: '#00FF00',
        pointBorderColor: '#00FF00',
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4,
        fill: false
      });
    }

    // Create chart with fixed tooltip configuration
    try {
      chartInstance.current = new Chart(ctx, {
        type: 'line',
        data: {
          datasets
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            duration: 2000,
            easing: 'easeOutQuart'
          },
          scales: {
            x: {
              type: 'time',
              time: {
                unit: 'month',
                displayFormats: {
                  month: 'MMM yyyy'
                }
              },
              title: {
                display: true,
                text: 'Date',
                color: '#ccc',
                font: {
                  family: 'Orbitron',
                  size: 14
                }
              },
              grid: {
                color: 'rgba(255, 255, 255, 0.1)'
              },
              ticks: {
                color: '#ccc',
                font: {
                  family: 'Roboto',
                  size: 12
                }
              }
            },
            y: {
              title: {
                display: true,
                text: 'Emissions (tons CO2e)',
                color: '#ccc',
                font: {
                  family: 'Orbitron',
                  size: 14
                }
              },
              grid: {
                color: 'rgba(255, 255, 255, 0.1)'
              },
              ticks: {
                color: '#ccc',
                font: {
                  family: 'Roboto',
                  size: 12
                }
              }
            }
          },
          plugins: {
            legend: {
              position: 'top',
              labels: {
                font: {
                  family: 'Orbitron',
                  size: 12
                },
                color: '#fff',
                padding: 20
              }
            },
            tooltip: {
              enabled: true,
              mode: 'index',
              intersect: false,
              backgroundColor: 'rgba(0, 0, 0, 0.8)',
              titleFont: {
                family: 'Orbitron',
                size: 14
              },
              bodyFont: {
                family: 'Roboto',
                size: 13
              },
              borderColor: 'rgba(0, 255, 255, 0.3)',
              borderWidth: 1,
              callbacks: {
                label: function(context) {
                  if (!context || !context.dataset || !context.parsed) {
                    return null;
                  }

                  const dataPoint = context.raw;
                  if (dataPoint && typeof dataPoint === 'object' && 'min' in dataPoint && 'max' in dataPoint) {
                    return [
                      `Min: ${dataPoint.min.toFixed(2)} tons CO2e`,
                      `Max: ${dataPoint.max.toFixed(2)} tons CO2e`
                    ];
                  }

                  if (context.parsed && typeof context.parsed.y === 'number') {
                    return `${context.dataset.label}: ${context.parsed.y.toFixed(2)} tons CO2e`;
                  }

                  return null;
                }
              }
            }
          },
          // Disable events that might be causing the tooltip error
          events: ['mousemove', 'mouseout', 'click', 'touchstart', 'touchmove'],
          // Add interaction configuration to prevent tooltip errors
          interaction: {
            mode: 'nearest',
            axis: 'x',
            intersect: false
          }
        }
      });
    } catch (error) {
      console.error("Error creating chart:", error);
    }
  }, [data, forecast, optimizedForecast]);

  return (
    <div className="forecast-chart-container">
      <h3 className="chart-title">Carbon Emissions Forecast</h3>
      <div className="chart-container">
        <canvas ref={chartRef}></canvas>
      </div>

      {impacts && Object.keys(impacts).length > 0 && (
        <div className="impact-factors">
          <h4>Key Impact Factors</h4>
          <div className="impact-list">
            {Object.entries(impacts)
              .sort((a, b) => Math.abs(b[1].impact_score) - Math.abs(a[1].impact_score))
              .slice(0, 3)
              .map(([factor, data], index) => {
                let factorName = factor;
                if (factor === 'energy_use') factorName = 'Energy Use';
                if (factor === 'transport') factorName = 'Transport';
                if (factor === 'waste') factorName = 'Waste';
                if (factor === 'water') factorName = 'Water';
                if (factor === 'fuel') factorName = 'Fuel';
                if (factor === 'grid_intensity') factorName = 'Grid Intensity';

                return (
                  <div key={factor} className="impact-item">
                    <div className="impact-rank">{index + 1}</div>
                    <div className="impact-info">
                      <div className="impact-name">{factorName}</div>
                      <div className="impact-score">
                        Impact Score: <span className="impact-value">{Math.abs(data.impact_score).toFixed(2)}</span>
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ForecastChart;
