import React, { useState, useEffect, useCallback } from 'react';
import { Line, Bar, Pie } from 'react-chartjs-2';
import './ModelPerformance.css';
import './ChartConfig';

const ModelPerformance = ({ data, forecast, impacts }) => {
  const [actualVsPredicted, setActualVsPredicted] = useState(null);
  const [errorMetrics, setErrorMetrics] = useState(null);
  const [regressorImportance, setRegressorImportance] = useState(null);
  const [isAnimated, setIsAnimated] = useState(false);

  // Prepare data for actual vs predicted chart
  const prepareActualVsPredictedData = useCallback(() => {
    if (!data || !forecast) return;

    // Get the last 12 months of actual data
    const actualData = data.slice(-12);

    // Format dates for display
    const labels = actualData.map(item => {
      const date = new Date(item.date || item.ds);
      return date.toLocaleDateString('en-US', { month: 'short', year: 'numeric' });
    });

    // Get actual emissions values
    const actualValues = actualData.map(item => item.emissions || item.y);

    // Get predicted values for the same period (if available)
    const predictedValues = forecast.slice(0, 12).map(item => item.predicted_emissions);

    // Create chart data
    const chartData = {
      labels,
      datasets: [
        {
          label: 'Actual Emissions',
          data: actualValues,
          borderColor: '#00FFFF',
          backgroundColor: 'rgba(0, 255, 255, 0.1)',
          borderWidth: 2,
          pointBackgroundColor: '#00FFFF',
          pointBorderColor: '#00FFFF',
          pointRadius: 4,
          pointHoverRadius: 6,
          tension: 0.4,
          fill: false
        },
        {
          label: 'Predicted Emissions',
          data: predictedValues,
          borderColor: '#FF00FF',
          backgroundColor: 'rgba(255, 0, 255, 0.1)',
          borderWidth: 2,
          pointBackgroundColor: '#FF00FF',
          pointBorderColor: '#FF00FF',
          pointRadius: 4,
          pointHoverRadius: 6,
          tension: 0.4,
          fill: false
        }
      ]
    };

    setActualVsPredicted(chartData);
  }, [data, forecast]);

  // Prepare error metrics data
  const prepareErrorMetricsData = useCallback(() => {
    if (!data || !forecast) return;

    // Calculate MAE and RMSE
    let mae = 0;
    let rmse = 0;
    let count = 0;

    // Get the last 12 months of actual data
    const actualData = data.slice(-12);

    // Get predicted values for the same period (if available)
    const forecastData = forecast.slice(0, 12);

    // Calculate errors
    actualData.forEach((actual, index) => {
      if (index < forecastData.length) {
        const actualValue = actual.emissions || actual.y;
        const predictedValue = forecastData[index].predicted_emissions;

        const error = Math.abs(actualValue - predictedValue);
        mae += error;
        rmse += error * error;
        count++;
      }
    });

    // Calculate final metrics
    mae = count > 0 ? mae / count : 0;
    rmse = count > 0 ? Math.sqrt(rmse / count) : 0;

    // Create chart data
    const chartData = {
      labels: ['MAE', 'RMSE'],
      datasets: [
        {
          label: 'Error Metrics',
          data: [mae, rmse],
          backgroundColor: [
            'rgba(0, 255, 255, 0.7)',
            'rgba(255, 0, 255, 0.7)'
          ],
          borderColor: [
            '#00FFFF',
            '#FF00FF'
          ],
          borderWidth: 1,
          hoverBackgroundColor: [
            'rgba(0, 255, 255, 0.9)',
            'rgba(255, 0, 255, 0.9)'
          ]
        }
      ]
    };

    setErrorMetrics(chartData);
  }, [data, forecast]);

  // Prepare regressor importance data
  const prepareRegressorImportanceData = useCallback(() => {
    if (!impacts) return;

    // Extract regressor impacts
    const labels = [];
    const values = [];
    const colors = [];

    // Generate neon colors
    const neonColors = [
      'rgba(0, 255, 255, 0.7)',   // Cyan
      'rgba(255, 0, 255, 0.7)',   // Magenta
      'rgba(0, 255, 0, 0.7)',     // Green
      'rgba(255, 255, 0, 0.7)',   // Yellow
      'rgba(255, 128, 0, 0.7)',   // Orange
      'rgba(128, 0, 255, 0.7)',   // Purple
      'rgba(0, 128, 255, 0.7)',   // Blue
      'rgba(255, 0, 128, 0.7)'    // Pink
    ];

    // Sort impacts by importance
    Object.entries(impacts).forEach(([key, impact], index) => {
      // Format key for display
      let displayKey = key;

      if (key === 'energy_kwh') displayKey = 'Energy Use';
      else if (key === 'transport_km') displayKey = 'Transport';
      else if (key === 'waste_kg') displayKey = 'Waste';
      else if (key === 'water_m3') displayKey = 'Water';
      else if (key === 'fuel_l') displayKey = 'Fuel';
      else if (key === 'grid_intensity') displayKey = 'Grid Intensity';
      else if (key === 'production_units') displayKey = 'Production';

      labels.push(displayKey);
      values.push(Math.abs(impact.impact_score));
      colors.push(neonColors[index % neonColors.length]);
    });

    // Create chart data
    const chartData = {
      labels,
      datasets: [
        {
          data: values,
          backgroundColor: colors,
          borderColor: colors.map(color => color.replace('0.7', '1')),
          borderWidth: 1,
          hoverBackgroundColor: colors.map(color => color.replace('0.7', '0.9')),
          hoverBorderColor: colors.map(color => color.replace('0.7', '1')),
        }
      ]
    };

    setRegressorImportance(chartData);
  }, [impacts]);

  useEffect(() => {
    if (data && forecast && impacts) {
      // Prepare data for actual vs predicted chart
      prepareActualVsPredictedData();

      // Prepare error metrics data
      prepareErrorMetricsData();

      // Prepare regressor importance data
      prepareRegressorImportanceData();

      // Trigger animation after data is loaded
      setTimeout(() => setIsAnimated(true), 300);
    }
  }, [data, forecast, impacts, prepareActualVsPredictedData, prepareErrorMetricsData, prepareRegressorImportanceData]);

  // Chart options
  const lineOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: isAnimated ? 2000 : 0,
      easing: 'easeOutQuart'
    },
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
      }
    },
    scales: {
      x: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        }
      },
      y: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        }
      }
    }
  };

  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: isAnimated ? 1500 : 0,
      easing: 'easeOutQuart'
    },
    plugins: {
      legend: {
        display: false
      }
    },
    scales: {
      x: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        }
      },
      y: {
        grid: {
          color: 'rgba(255, 255, 255, 0.1)'
        }
      }
    }
  };

  const pieOptions = {
    responsive: true,
    maintainAspectRatio: false,
    animation: {
      duration: isAnimated ? 1800 : 0,
      easing: 'easeOutQuart'
    },
    plugins: {
      legend: {
        position: 'right',
        labels: {
          padding: 15
        }
      }
    }
  };

  return (
    <div className="model-performance-container">
      <div className="performance-header">
        <h2>Model Performance Analysis</h2>
        <p className="performance-subtitle">
          Analyzing Prophet model accuracy and factor importance
        </p>
      </div>

      <div className="performance-grid">
        <div className="chart-card">
          <h3>Actual vs. Predicted Emissions</h3>
          <div className="chart-container">
            {actualVsPredicted ? (
              <Line data={actualVsPredicted} options={lineOptions} />
            ) : (
              <div className="loading-chart">Loading chart data...</div>
            )}
          </div>
        </div>

        <div className="chart-card">
          <h3>Error Metrics</h3>
          <div className="chart-container">
            {errorMetrics ? (
              <Bar data={errorMetrics} options={barOptions} />
            ) : (
              <div className="loading-chart">Loading chart data...</div>
            )}
          </div>
          <div className="metric-explanation">
            <p><strong>MAE:</strong> Mean Absolute Error - Average absolute difference between actual and predicted values</p>
            <p><strong>RMSE:</strong> Root Mean Square Error - Square root of the average squared differences</p>
          </div>
        </div>

        <div className="chart-card">
          <h3>Factor Importance</h3>
          <div className="chart-container pie-container">
            {regressorImportance ? (
              <Pie data={regressorImportance} options={pieOptions} />
            ) : (
              <div className="loading-chart">Loading chart data...</div>
            )}
          </div>
          <div className="importance-explanation">
            <p>Relative importance of each factor in predicting carbon emissions</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModelPerformance;
