import React, { useState, useEffect } from 'react';
import { useTable } from 'react-table';
import './DataTable.css';

const DataTable = ({ data, onDataUpdate, summary, readOnly = false, isForecasted = false }) => {
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    if (data && data.length > 0) {
      setTableData(data.map((row, index) => ({ ...row, id: row.id || index })));
    } else {
      setTableData([]);
    }
  }, [data]);

  // Define columns based on data structure
  const getColumns = () => {
    if (!tableData || tableData.length === 0) return [];

    const firstRow = tableData[0];
    const columns = Object.keys(firstRow)
      .filter(key => key !== 'id') // Exclude id from display
      .map(key => {
        // Determine if column is numeric
        const isNumeric = ['energy_use', 'transport', 'waste', 'water', 'fuel', 
                           'emissions', 'production', 'grid_intensity', 
                           'predicted_emissions', 'lower_bound', 'upper_bound'].includes(key);

        return {
          Header: key.split('_').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' '),
          accessor: key,
          Cell: ({ value, row, column }) => {
            if (readOnly) {
              // Format date if it's a date column
              if (column.id === 'date' || column.id === 'ds') {
                return new Date(value).toLocaleDateString();
              }

              // Format numeric values
              if (isNumeric) {
                return typeof value === 'number' ? value.toFixed(2) : value;
              }

              return value;
            }

            return (
              <input
                type={isNumeric ? 'number' : 'text'}
                value={value || ''}
                onChange={e => {
                  const updatedData = [...tableData];
                  const rowIndex = updatedData.findIndex(r => r.id === row.original.id);

                  if (rowIndex !== -1) {
                    updatedData[rowIndex] = {
                      ...updatedData[rowIndex],
                      [column.id]: isNumeric ? 
                        (e.target.value === '' ? '' : parseFloat(e.target.value)) : 
                        e.target.value
                    };

                    setTableData(updatedData);
                    if (onDataUpdate) onDataUpdate(updatedData);
                  }
                }}
                className={`cell-input ${isNumeric ? 'numeric' : ''}`}
                disabled={readOnly}
              />
            );
          }
        };
      });

    return columns;
  };

  const columns = React.useMemo(() => getColumns(), [tableData, readOnly, getColumns]);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable({ columns, data: tableData });

  // Add a new row to the table
  const handleAddRow = () => {
    if (!tableData || tableData.length === 0) return;

    const newRow = {};
    const lastRow = tableData[tableData.length - 1];

    // Initialize with empty values
    Object.keys(lastRow).forEach(key => {
      if (key === 'id') {
        newRow[key] = Date.now(); // Use timestamp as unique id
      } else if (key === 'date') {
        // Set date to today
        newRow[key] = new Date().toISOString().split('T')[0];
      } else {
        newRow[key] = '';
      }
    });

    const updatedData = [...tableData, newRow];
    setTableData(updatedData);
    if (onDataUpdate) onDataUpdate(updatedData);
  };

  // Delete a row from the table
  const handleDeleteRow = (id) => {
    const updatedData = tableData.filter(row => row.id !== id);
    setTableData(updatedData);
    if (onDataUpdate) onDataUpdate(updatedData);
  };

  // Ensure all numeric values are properly converted before sending to backend
  const prepareDataForBackend = (data) => {
    const numericColumns = ['energy_use', 'transport', 'waste', 'water', 'fuel', 
                           'emissions', 'production', 'grid_intensity'];

    return data.map(row => {
      const newRow = {...row};
      numericColumns.forEach(col => {
        if (col in newRow && newRow[col] !== '') {
          newRow[col] = parseFloat(newRow[col]);
        }
      });
      return newRow;
    });
  };

  // Override the onDataUpdate to ensure proper data conversion
  const handleDataUpdate = (updatedData) => {
    if (onDataUpdate) {
      const preparedData = prepareDataForBackend(updatedData);
      onDataUpdate(preparedData);
    }
  };

  return (
    <div className="data-table-container">
      {summary && !isForecasted && (
        <div className="data-summary">
          <h3>Data Summary</h3>
          <div className="summary-grid">
            <div className="summary-item">
              <span className="summary-label">Total Rows:</span>
              <span className="summary-value">{summary.total_rows}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Departments:</span>
              <span className="summary-value">{summary.departments.join(', ')}</span>
            </div>
          </div>
          <div className="summary-stats">
            <table className="summary-table">
              <thead>
                <tr>
                  <th>Metric</th>
                  <th>Mean</th>
                  <th>Min</th>
                  <th>Max</th>
                </tr>
              </thead>
              <tbody>
                {Object.keys(summary.mean).map(key => (
                  <tr key={key}>
                    <td>{key.split('_').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')}</td>
                    <td>{summary.mean[key].toFixed(2)}</td>
                    <td>{summary.min[key].toFixed(2)}</td>
                    <td>{summary.max[key].toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {tableData.length > 0 ? (
        <div className="table-wrapper">
          <table {...getTableProps()} className="data-table">
            <thead>
              {headerGroups.map(headerGroup => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map(column => (
                    <th {...column.getHeaderProps()}>
                      {column.render('Header')}
                    </th>
                  ))}
                  {!readOnly && (
                    <th className="action-column">Actions</th>
                  )}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {rows.map(row => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()} className="data-row">
                    {row.cells.map(cell => (
                      <td {...cell.getCellProps()} className={isForecasted && cell.column.id === 'predicted_emissions' ? 'highlighted-cell' : ''}>
                        {cell.render('Cell')}
                      </td>
                    ))}
                    {!readOnly && (
                      <td className="action-column">
                        <button 
                          className="delete-btn" 
                          onClick={() => handleDeleteRow(row.original.id)}
                        >
                          Ã—
                        </button>
                      </td>
                    )}
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="no-data">No data available. Upload a file or add data manually.</div>
      )}

      {!readOnly && (
        <div className="table-actions">
          <button className="btn btn-secondary" onClick={handleAddRow}>
            Add Row
          </button>
        </div>
      )}
    </div>
  );
};

export default DataTable;
