import React, { useEffect, useRef } from 'react';
import { Chart, registerables } from 'chart.js';
import './ForecastChart.css';

// Register Chart.js components
Chart.register(...registerables);

const ForecastChart = ({ actualData, forecastData, optimizedData }) => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  useEffect(() => {
    if (!actualData || !forecastData) return;

    // Destroy previous chart if it exists
    if (chartInstance.current) {
      chartInstance.current.destroy();
    }

    const ctx = chartRef.current.getContext('2d');

    // Prepare data
    const actualDates = actualData.map(d => new Date(d.date));
    const actualValues = actualData.map(d => {
      // Ensure emissions is a number
      const val = d.emissions;
      return typeof val === 'number' ? val : parseFloat(val) || 0;
    });

    const forecastDates = forecastData.map(d => new Date(d.date));
    const forecastValues = forecastData.map(d => {
      // Ensure predicted_emissions is a number
      const val = d.predicted_emissions;
      return typeof val === 'number' ? val : parseFloat(val) || 0;
    });

    const lowerBounds = forecastData.map(d => {
      // Ensure lower_bound is a number
      const val = d.lower_bound;
      return typeof val === 'number' ? val : parseFloat(val) || 0;
    });

    const upperBounds = forecastData.map(d => {
      // Ensure upper_bound is a number
      const val = d.upper_bound;
      return typeof val === 'number' ? val : parseFloat(val) || 0;
    });

    // Prepare optimized data if available
    const optimizedValues = optimizedData ? optimizedData.map(d => {
      // Ensure predicted_emissions is a number
      const val = d.predicted_emissions;
      return typeof val === 'number' ? val : parseFloat(val) || 0;
    }) : null;

    // Create datasets
    const datasets = [
      {
        label: 'Actual Emissions',
        data: actualValues,
        borderColor: '#00FFFF', // Cyan
        backgroundColor: 'rgba(0, 255, 255, 0.1)',
        borderWidth: 2,
        pointBackgroundColor: '#00FFFF',
        pointBorderColor: '#00FFFF',
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4
      },
      {
        label: 'Forecasted Emissions',
        data: Array(actualValues.length).fill(null).concat(forecastValues),
        borderColor: '#FF00FF', // Magenta
        backgroundColor: 'rgba(255, 0, 255, 0.1)',
        borderWidth: 2,
        pointBackgroundColor: '#FF00FF',
        pointBorderColor: '#FF00FF',
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4
      },
      {
        label: 'Upper Bound',
        data: Array(actualValues.length).fill(null).concat(upperBounds),
        borderColor: 'rgba(255, 0, 255, 0.3)',
        backgroundColor: 'transparent',
        borderWidth: 1,
        borderDash: [5, 5],
        pointRadius: 0,
        fill: '+1'
      },
      {
        label: 'Lower Bound',
        data: Array(actualValues.length).fill(null).concat(lowerBounds),
        borderColor: 'rgba(255, 0, 255, 0.3)',
        backgroundColor: 'rgba(255, 0, 255, 0.1)',
        borderWidth: 1,
        borderDash: [5, 5],
        pointRadius: 0,
        fill: false
      }
    ];

    // Add optimized forecast if available
    if (optimizedValues) {
      datasets.push({
        label: 'Optimized Emissions',
        data: Array(actualValues.length).fill(null).concat(optimizedValues),
        borderColor: '#00FF00', // Green
        backgroundColor: 'rgba(0, 255, 0, 0.1)',
        borderWidth: 2,
        pointBackgroundColor: '#00FF00',
        pointBorderColor: '#00FF00',
        pointRadius: 4,
        pointHoverRadius: 6,
        tension: 0.4
      });
    }

    // All dates combined for x-axis
    const allDates = [...actualDates, ...forecastDates];

    // Create chart
    chartInstance.current = new Chart(ctx, {
      type: 'line',
      data: {
        labels: allDates,
        datasets: datasets
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        animation: {
          duration: 2000,
          easing: 'easeOutQuart'
        },
        scales: {
          x: {
            type: 'time',
            time: {
              unit: 'month',
              displayFormats: {
                month: 'MMM yyyy'
              }
            },
            grid: {
              color: 'rgba(255, 255, 255, 0.1)'
            },
            ticks: {
              color: '#CCC'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Emissions (tons CO2e)',
              color: '#CCC'
            },
            grid: {
              color: 'rgba(255, 255, 255, 0.1)'
            },
            ticks: {
              color: '#CCC'
            }
          }
        },
        plugins: {
          legend: {
            position: 'top',
            labels: {
              color: '#CCC',
              font: {
                family: "'Orbitron', sans-serif",
                size: 12
              },
              boxWidth: 15,
              usePointStyle: true
            }
          },
          tooltip: {
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
            titleFont: {
              family: "'Orbitron', sans-serif"
            },
            bodyFont: {
              family: "'Roboto', sans-serif"
            },
            borderColor: 'rgba(0, 255, 255, 0.3)',
            borderWidth: 1,
            callbacks: {
              label: function(context) {
                let label = context.dataset.label || '';
                if (label) {
                  label += ': ';
                }
                if (context.parsed.y !== null) {
                  label += context.parsed.y.toFixed(2) + ' tons CO2e';
                }
                return label;
              }
            }
          }
        }
      }
    });

    // Apply animation to draw lines
    chartInstance.current.options.animation = {
      onComplete: function() {
        const meta = chartInstance.current.getDatasetMeta(0);

        if (meta.controller.chart.ctx) {
          meta.controller.chart.ctx.save();
          meta.controller.chart.ctx.strokeStyle = '#00FFFF';
          meta.controller.chart.ctx.lineWidth = 2;
          meta.controller.chart.ctx.setLineDash([]);

          meta.controller.chart.ctx.beginPath();
          meta.data.forEach(point => {
            meta.controller.chart.ctx.lineTo(point.x, point.y);
          });
          meta.controller.chart.ctx.stroke();

          meta.controller.chart.ctx.restore();
        }
      }
    };

    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
      }
    };
  }, [actualData, forecastData, optimizedData]);

  return (
    <div className="chart-container">
      <div className="chart-wrapper">
        <canvas ref={chartRef}></canvas>
      </div>
      <div className="chart-legend">
        <div className="legend-item">
          <div className="legend-color" style={{ backgroundColor: '#00FFFF' }}></div>
          <div className="legend-label">Actual Emissions</div>
        </div>
        <div className="legend-item">
          <div className="legend-color" style={{ backgroundColor: '#FF00FF' }}></div>
          <div className="legend-label">Forecasted Emissions</div>
        </div>
        {optimizedData && (
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#00FF00' }}></div>
            <div className="legend-label">Optimized Emissions</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ForecastChart;
