import React, { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import 'chartjs-adapter-date-fns';
import './ForecastChart.css';

const ForecastChart = ({ data, forecast, optimizedForecast, impacts }) => {
  const chartRef = useRef(null);
  const chartInstance = useRef(null);

  // Clean up chart on unmount
  useEffect(() => {
    return () => {
      if (chartInstance.current) {
        chartInstance.current.destroy();
        chartInstance.current = null;
      }
    };
  }, []);

  useEffect(() => {
    if (!forecast || !chartRef.current) return;

    // Destroy previous chart if it exists
    if (chartInstance.current) {
      chartInstance.current.destroy();
      chartInstance.current = null;
    }

    try {
      const ctx = chartRef.current.getContext('2d');

      // Process data to ensure one point per month
      const processedData = processMonthlyData(data);
      const processedForecast = processForecastData(forecast);
      const processedOptimized = optimizedForecast ? processForecastData(optimizedForecast) : [];

      // Create chart with extra padding for first point
      chartInstance.current = new Chart(ctx, {
        type: 'bar', // Use bar as base type for more control
        data: {
          labels: generateMonthLabels(processedData, processedForecast, processedOptimized),
          datasets: [
            // Actual data
            {
              type: 'line',
              label: 'Actual Emissions',
              data: processedData.map(d => d.emissions),
              borderColor: '#00FFFF', // Cyan
              backgroundColor: 'rgba(0, 255, 255, 0.1)',
              borderWidth: 3,
              pointBackgroundColor: '#00FFFF',
              pointBorderColor: '#000',
              pointRadius: 9,
              pointHoverRadius: 11,
              tension: 0.4,
              fill: false,
              clip: false // Prevent clipping of points at edges
            },
            // Forecast data
            {
              type: 'line',
              label: 'Forecast Emissions',
              data: processedForecast.map(d => d.emissions),
              borderColor: '#FF00FF', // Magenta
              backgroundColor: 'rgba(255, 0, 255, 0.1)',
              borderWidth: 3,
              pointBackgroundColor: '#FF00FF',
              pointBorderColor: '#000',
              pointRadius: 9,
              pointHoverRadius: 11,
              tension: 0.4,
              fill: false,
              clip: false // Prevent clipping of points at edges
            },
            // Upper bound
            {
              type: 'line',
              label: 'Upper Bound',
              data: processedForecast.map(d => d.upper),
              borderColor: 'rgba(255, 0, 255, 0.3)',
              backgroundColor: 'transparent',
              borderWidth: 1,
              borderDash: [5, 5],
              pointRadius: 0,
              fill: false,
              clip: false
            },
            // Lower bound
            {
              type: 'line',
              label: 'Lower Bound',
              data: processedForecast.map(d => d.lower),
              borderColor: 'rgba(255, 0, 255, 0.3)',
              backgroundColor: 'rgba(255, 0, 255, 0.1)',
              borderWidth: 1,
              borderDash: [5, 5],
              pointRadius: 0,
              fill: '+1',
              clip: false
            }
          ].concat(
            // Add optimized forecast if available
            optimizedForecast ? [
              {
                type: 'line',
                label: 'Optimized Emissions',
                data: processedOptimized.map(d => d.emissions),
                borderColor: '#00FF00', // Green
                backgroundColor: 'rgba(0, 255, 0, 0.1)',
                borderWidth: 3,
                borderDash: [5, 5],
                pointBackgroundColor: '#00FF00',
                pointBorderColor: '#000',
                pointRadius: 9,
                pointHoverRadius: 11,
                tension: 0.4,
                fill: false,
                clip: false // Prevent clipping of points at edges
              }
            ] : []
          )
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          animation: {
            duration: 2000,
            easing: 'easeOutQuart'
          },
          scales: {
            x: {
              title: {
                display: true,
                text: 'Month',
                color: '#fff',
                font: {
                  family: 'Orbitron',
                  size: 16,
                  weight: 'bold'
                },
                padding: {top: 10, bottom: 10}
              },
              grid: {
                color: 'rgba(255, 255, 255, 0.1)',
                tickLength: 10
              },
              ticks: {
                color: '#fff',
                font: {
                  family: 'Roboto',
                  size: 12,
                  weight: 'bold'
                },
                padding: 15,
                maxRotation: 45,
                minRotation: 45
              },
              offset: true, // Add offset to ensure bars don't overlap with axis
              // Ensure extra space at the beginning and end
              afterFit: function(scale) {
                scale.paddingLeft = 50;  // Add extra padding to the left
                scale.paddingRight = 50; // Add extra padding to the right
              }
            },
            y: {
              title: {
                display: true,
                text: 'Emissions (tons CO2e)',
                color: '#fff',
                font: {
                  family: 'Orbitron',
                  size: 16,
                  weight: 'bold'
                },
                padding: {top: 10, left: 10, right: 10, bottom: 10}
              },
              grid: {
                color: 'rgba(255, 255, 255, 0.1)'
              },
              ticks: {
                color: '#fff',
                font: {
                  family: 'Roboto',
                  size: 12,
                  weight: 'bold'
                },
                padding: 15,
                callback: function(value) {
                  return value.toFixed(1);
                }
              },
              beginAtZero: false,
              // Ensure extra space at the top and bottom
              afterFit: function(scale) {
                scale.paddingTop = 20;    // Add extra padding to the top
                scale.paddingBottom = 20; // Add extra padding to the bottom
              }
            }
          },
          plugins: {
            legend: {
              position: 'top',
              labels: {
                font: {
                  family: 'Orbitron',
                  size: 12,
                  weight: 'bold'
                },
                color: '#fff',
                padding: 20,
                usePointStyle: true,
                pointStyle: 'circle',
                filter: function(item) {
                  // Hide bounds from legend
                  return !['Upper Bound', 'Lower Bound'].includes(item.text);
                }
              }
            },
            tooltip: {
              enabled: true,
              mode: 'index',
              intersect: false,
              backgroundColor: 'rgba(0, 0, 0, 0.8)',
              titleFont: {
                family: 'Orbitron',
                size: 14,
                weight: 'bold'
              },
              bodyFont: {
                family: 'Roboto',
                size: 13
              },
              borderColor: 'rgba(0, 255, 255, 0.5)',
              borderWidth: 1,
              padding: 12,
              callbacks: {
                title: function(tooltipItems) {
                  return tooltipItems[0].label;
                },
                label: function(context) {
                  let label = context.dataset.label || '';

                  if (label) {
                    label += ': ';
                  }

                  if (context.parsed.y !== null) {
                    label += context.parsed.y.toFixed(2) + ' tons CO2e';
                  }

                  return label;
                }
              }
            }
          },
          elements: {
            point: {
              radius: 9,
              hoverRadius: 11,
              hitRadius: 15,
              borderWidth: 2
            },
            line: {
              tension: 0.4
            }
          },
          layout: {
            padding: {
              left: 50,
              right: 50,
              top: 50,
              bottom: 50
            }
          }
        }
      });
    } catch (error) {
      console.error("Error creating chart:", error);
    }
  }, [data, forecast, optimizedForecast]);

  // Process actual data to ensure one point per month
  const processMonthlyData = (data) => {
    if (!data || data.length === 0) return [];

    // Group by month
    const monthlyData = {};

    data.forEach(item => {
      if (!item.date || !item.emissions) return;

      const date = new Date(item.date);
      const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;

      if (!monthlyData[monthKey]) {
        monthlyData[monthKey] = {
          month: monthKey,
          monthLabel: `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`,
          emissions: 0
        };
      }

      monthlyData[monthKey].emissions += parseFloat(item.emissions) || 0;
    });

    // Convert to array and sort by month
    return Object.values(monthlyData)
      .sort((a, b) => a.month.localeCompare(b.month));
  };

  // Process forecast data
  const processForecastData = (forecastData) => {
    if (!forecastData || forecastData.length === 0) return [];

    return forecastData.map(item => {
      const date = new Date(item.ds);
      return {
        month: `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`,
        monthLabel: `${date.toLocaleString('default', { month: 'long' })} ${date.getFullYear()}`,
        emissions: item.predicted_emissions,
        upper: item.upper_bound,
        lower: item.lower_bound
      };
    }).sort((a, b) => a.month.localeCompare(b.month));
  };

  // Generate month labels for x-axis
  const generateMonthLabels = (actualData, forecastData, optimizedData) => {
    // Combine all months
    const allMonths = [
      ...actualData.map(d => ({ month: d.month, label: d.monthLabel })),
      ...forecastData.map(d => ({ month: d.month, label: d.monthLabel })),
      ...optimizedData.map(d => ({ month: d.month, label: d.monthLabel }))
    ];

    // Remove duplicates and sort
    const uniqueMonths = Array.from(
      new Map(allMonths.map(item => [item.month, item.label])).values()
    ).sort();

    return uniqueMonths;
  };

  return (
    <div className="forecast-chart-container">
      <h3 className="chart-title">Monthly Carbon Emissions Forecast</h3>
      <div className="chart-container" style={{ height: '500px' }}>
        <canvas ref={chartRef}></canvas>
      </div>

      {impacts && Object.keys(impacts).length > 0 && (
        <div className="impact-factors">
          <h4>Key Impact Factors</h4>
          <div className="impact-list">
            {Object.entries(impacts)
              .sort((a, b) => Math.abs(b[1].impact_score) - Math.abs(a[1].impact_score))
              .slice(0, 3)
              .map(([factor, data], index) => {
                let factorName = factor;
                if (factor === 'energy_use') factorName = 'Energy Use';
                if (factor === 'transport') factorName = 'Transport';
                if (factor === 'waste') factorName = 'Waste';
                if (factor === 'water') factorName = 'Water';
                if (factor === 'fuel') factorName = 'Fuel';
                if (factor === 'grid_intensity') factorName = 'Grid Intensity';

                return (
                  <div key={factor} className="impact-item">
                    <div className="impact-rank">{index + 1}</div>
                    <div className="impact-info">
                      <div className="impact-name">{factorName}</div>
                      <div className="impact-score">
                        Impact Score: <span className="impact-value">{Math.abs(data.impact_score).toFixed(2)}</span>
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>
      )}
    </div>
  );
};

export default ForecastChart;
